        private void renameItem(object sender, EventArgs e)
        {
            this.listView1.LabelEdit = true;
            foreach (ListViewItem listViewItem in selectedListItems)
            {
                listViewItem.BeginEdit();
            }
        }

        private void MenuItem_Click(object sender, EventArgs e)
        {
            throw new NotImplementedException();
        }

        private void listviewEventHandler(object sender, MouseEventArgs args)
        {
            if(args.Button == MouseButtons.Right)
            {
                Console.WriteLine(sender.ToString());
                selectedListItems = ((ListView)sender).SelectedItems;
                this.contextMenuStrip.Show(this.listView1, args.Location);
            }
        }

        private void removeItem(object sender, EventArgs args)
        {
            foreach (ListViewItem listViewItem in selectedListItems){
                listView1.Items.Remove(listViewItem);
            }
        }

        private void startApplication(object sender, EventArgs args)
        {
            foreach (ListViewItem listViewItem in selectedListItems)
            {
                Process.Start((string)listViewItem.Tag);
            }
        }

